import { FormControl } from "react-bootstrap";
import React,{useState} from 'react';
function Register(){
    const [name,setName]=useState("");
    const [email,setEmail]=useState("");
    const [password,setPassword]=useState("");
   async function signUp(){
        let new_user={name,email,password}
        let result=await fetch("http://localhost:8080/user/registration",{
            method:'POST',
            headers:{
                "Content-Type":"application/json",
                "Accept":"application/json"
            },
            body:JSON.stringify(new_user)
        });
        result=await result.json();
        console.warn("result",result)

    }
    return (
        <div className="col-sm-6 offset-sm-3">
            <h1>User Sign Up</h1>
            <br/>
            <h3 className="form-control">Name:</h3> <input type="text" value={name} onChange={(e)=>setName(e.target.value)} className="form-control"/>
            <br/>
            Email: <input type="text" value={email} onChange={(e)=>setEmail(e.target.value)} className="form-control"/>
            <br/>
            Password: <input type="text" value ={password} onChange={(e)=>setPassword(e.target.value)} className="form-control"/>
            <br/>
            <button className="btn btn-primary" onClick={signUp}>Sign UP</button>
        </div>
    );
}
export default Register;
